mapper.select:
SELECT  count(id)
FROM  `Ship`
where `isDeleted` = 0
 AND `id` != #{pojo.id} 
 AND `shipNo` = #{pojo.shipNo} 
limit 1

mapper.sql:
from SailingInfo a LEFT JOIN DispatchInfo d on a.orderId = d.id LEFT JOIN CustomerTaskFlow b on d.customerTaskFlowId = b.id LEFT JOIN CustomerTask ct on b.taskId = ct.id LEFT JOIN Contract c on ct.contractId = c.id LEFT JOIN ContractFlow e on e.contractId = c.id LEFT JOIN ShipOil so ON so.disId = d.id LEFT JOIN PreBuckleOil po ON po.disId = d.id where a.isDeleted = '0' and a.status = '5'
AND (a.`shipId` in ( select id from Ship where shipNo like concat('%', #{pojo.key}, '%') ))
and FROM_UNIXTIME( a.loadTime, '%Y-%m' ) >= #{pojo.startTime}
and FROM_UNIXTIME( a.loadTime, '%Y-%m' ) <= #{pojo.endTime}

from SailingInfo a LEFT JOIN DispatchInfo d on a.orderId = d.id LEFT JOIN CustomerTaskFlow b on d.customerTaskFlowId = b.id LEFT JOIN CustomerTask ct on b.taskId = ct.id LEFT JOIN Contract c on ct.contractId = c.id LEFT JOIN ContractFlow e on e.contractId = c.id where a.isDeleted = '0' and a.status = '5'
AND (a.`shipId` in ( select id from Ship where shipNo like concat('%', #{pojo.key}, '%') ))
and FROM_UNIXTIME( a.dischargeTime, '%Y-%m' ) >= #{pojo.startTime}
and FROM_UNIXTIME( a.dischargeTime, '%Y-%m' ) <= #{pojo.endTime}

where a.`isDeleted` = 0 and a.`shipFlag` < 3
 AND a.`id` = #{pojo.id} 

    AND (a.`shipNo` like concat('%', #{pojo.key}, '%') )

 AND a.`fitStatus` = #{pojo.fitStatus}
